import axios from "axios";

export function getDietTypes() {
  return function (dispatch) {
    return axios
      .get("http://localhost:3001/types")
      .then((response) => {
        dispatch({
          type: "GET_DIET_TYPES",
          payload: response.data.map((e) => e.name),
        });
      })
      .catch((err) =>
        dispatch({
          type: "SERVER _ERR",
          payload: err,
        })
      );
  };
}

export function searchByName(name) {
  return function (dispatch) {
    return axios
      .get(`http://localhost:3001/recipes?name=${name}`)
      .then((response) => {
        dispatch({
          type: "GET_SEARCH_RESULTS",
          payload: response.data,
        });
      })
      .catch((err) => {
        if ((err.response.status = "404")) {
          return dispatch({
            type: "FOUND_RESULTS",
          });
        }
        dispatch({
          type: "FOUND_RESULTS",
        });
      });
  };
}
export function searchBy() {
  return function (dispatch) {
    return axios
      .get(`http://localhost:3001/recipes`)
      .then((response) => {
        dispatch({
          type: "GET_SEARCH_RESULTS",
          payload: response.data,
        });
      })
      .catch((err) => {
        console.log(err);
        if ((err.response.status = "404")) {
          return dispatch({
            type: "FOUND_RESULTS",
          });
        }
        dispatch({
          type: "FOUND_RESULTS",
        });
      });
  };
}

export function postRecipe(obj) {
  return function (dispatch) {
    return axios
      .post("http://localhost:3001/recipe", {
        title: obj.name,
        summary: obj.summary,
        score: obj.score,
        healthscore: obj.healthScore,
        instructions: obj.steps,
        dietTypes: obj.dietTypesArray,
      })
      .then((res) => {
        return dispatch({
          type: "POST_ERROR",
          payload: false,
        });
      })
      .catch((err) => {
        return dispatch({
          type: "POST_ERROR",
          payload: true,
        });
      });
  };
}

export function setLoading(param) {
  return function (dispatch) {
    dispatch({
      type: "CHANGE_LOADING",
      payload: param,
    });
  };
}

export function setPageNumbers(array) {
  return function (dispatch) {
    dispatch({
      type: "SET_PAGE_NUMBERS",
      payload: array,
    });
  };
}

export function setCurrentPage(currentPage) {
  return function (dispatch) {
    dispatch({
      type: "SET_CURRENT_PAGE",
      payload: currentPage,
    });
  };
}
export function orderResults(results, order) {
  return function (dispatch) {
    dispatch({
      type: `ORDER_${order}`,
      payload: results,
    });
  };
}
export function setOrderBy(order) {
  return function (dispatch) {
    dispatch({
      type: "SET_ORDER",
      payload: order,
    });
  };
}

export function setFilter(filter) {
  return function (dispatch) {
    dispatch({
      type: "SET_FILTER",
      payload: filter,
    });
  };
}
