import React, { useState } from "react";
import { Link } from "react-router-dom";
import navStyles from "./Nav.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faTimes, faSearch } from "@fortawesome/free-solid-svg-icons";

const Nav = () => {
  const [toggle, setToggle] = useState(false);

  const toggleHandler = () => {
    setToggle(!toggle);
  };
  return (
    <nav className={navStyles.navbar}>
      <div className={navStyles.logo}>Food App</div>
      <div className={navStyles.navMenuOptions}>
        <ul
          className={
            toggle
              ? `${navStyles.navMenu} ${navStyles.active}`
              : `${navStyles.navMenu}`
          }
        >
          <li className={navStyles.navLink}>
            <Link to="/home"> Home </Link>
          </li>

          <li className={navStyles.navLink}>
            <Link to="/about"> About </Link>
          </li>

          <li className={navStyles.navLink}>
            <Link to="/addRecipe">Add a Recipe</Link>
          </li>
        </ul>

        {!toggle && (
          <FontAwesomeIcon
            icon={faBars}
            className={navStyles.barIcon}
            onClick={toggleHandler}
          />
        )}
        {toggle && (
          <FontAwesomeIcon
            icon={faTimes}
            className={navStyles.barIcon}
            onClick={toggleHandler}
          />
        )}
        <FontAwesomeIcon icon={faSearch} className={navStyles.searchIcon} />
      </div>
    </nav>
  );
};

export default Nav;
